export type SuiNetworkName = 'mainnet' | 'testnet' | 'devnet';
/**
 * Messages exchanged for sui requests.
 */
export type SuiMessages = {
    sui_signMessage: (arg: {
        message: string;
        walletId: string;
    }) => Promise<{
        signature: string;
    }>;
    sui_signTransaction: (arg: {
        transaction: string;
        walletId: string;
    }) => Promise<{
        signature: string;
    }>;
    sui_getNetworkName: (arg: {
        walletId: string;
    }) => Promise<SuiNetworkName>;
};
