'use client'
import { useEffect } from 'react';
import { getCore } from '@dynamic-labs-sdk/client/core';
import { useProjectSettings } from '../../../../store/state/projectSettings/projectSettings.js';
import { getDynamicClient } from '../../../client.js';

const useSyncProjectSettingsWithVanillaClient = () => {
    const projectSettings = useProjectSettings();
    /**
     * This useEffect is used to sync the project settings to vanilla SDK.
     */
    useEffect(() => {
        getCore(getDynamicClient()).state.set({
            projectSettings: projectSettings !== null && projectSettings !== void 0 ? projectSettings : null,
        });
    }, [projectSettings]);
};

export { useSyncProjectSettingsWithVanillaClient };
