// eslint-disable-next-line @typescript-eslint/no-explicit-any
const useRpcProviders = (selector) => {
    const { networkConfigurations, walletConnectorOptions } = useInternalDynamicContext();
    return useMemo(() => {
        if (!networkConfigurations)
            return selector(undefined);
        const providers = ChainRpcProviders.getProviders(networkConfigurations);
        return selector(providers);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [
        /**
         * The `walletConnectorOptions.length` is included in the dependency array to ensure that
         * the providers are re-calculated whenever the WalletConnector's initialization state changes.
         * This avoids a potential race condition where the `useRpcProviders` hook might return
         * incomplete or empty providers if it executes before the WalletConnector has been fully initialized.
         * Since the `ChainRpcProviders` are registered during WalletConnector initialization,
         * this dependency ensures accurate and up-to-date provider data.
         */
        walletConnectorOptions.length,
        networkConfigurations,
        selector,
    ]);
};

export { useRpcProviders };
